mapper.insert:
INSERT INTO users_table(

	) VALUES (
		#{userNumber}
		, #{userNickname}
		, #{userId}
		, #{userPassword}
		, #{userEmail}
		, #{userRegion}
		, NOW()
		, false
		, NULL
		, false
	)

mapper.select:
select user_id
from users_table
where user_id = #{userId};

select user_nickname
from users_table
where user_nickname = #{userNickname};

select user_email
from users_table
where user_email = #{userEmail};

mapper.update:
update users_table
set user_nickname = #{userNickname}
	, user_region = #{userRegion}
where user_id = #{userId};

update users_table
set user_password = #{userPassword}
	, user_nickname = #{userNickname}
	, user_region = #{userRegion}
where user_id = #{userId};

mapper.select:
select user_id 
from users_table
where user_email = #{userEmail};
